import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import WeatherForecast from './WeatherForecast.js';
import WeatherWidget from './weather_app_component/weatheWidget.js';

const key = 'cdc11e06be4ca20a5d82b9327816fbe2';
class App extends React.Component{
  constructor(){
    super();
    this.state={
      city:undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius:undefined,
      maxtemp:undefined,
      mintemp:undefined,
      desc:undefined,

    };
    this.getWeather();
  }


  getWeather = async() =>{
    const weatherapi = await fetch('https://api.openweathermap.org/data/2.5/weather?q=Ahmedabad,in&APPID=' +key+ '&units=metric')
    const response = await weatherapi.json();
    console.log(response);

    this.setState({
      city : response.name,
      country : response.sys.country,
      icon: undefined,
      main: response.main,
      celsius:response.main.temp,
      maxtemp:response.main.temp_max,
      mintemp:response.main.temp_min,
      desc:response.weather[0].desc,
    })
  }



  render(){
    return(
      <div className="App">
      <header className="App-header">
        <WeatherWidget
         city={this.state.city} 
         country={this.state.country}
         temp_celsius={this.state.celsius}
         maxtemp = {this.state.maxtemp}
         mintemp = {this.state.mintemp}
         desc= {this.state.desc}
         ></WeatherWidget>
      </header>
    </div>
    );
  }
}




export default App;
