import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import WeatherForecast from './WeatherForecast.js';
import WeatherWidget from './weather_app_component/weatheWidget.js';

const key = 'cdc11e06be4ca20a5d82b9327816fbe2';
class App extends React.Component{
  constructor(){
    super();
    this.state={
      city:undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius:undefined,
      maxtemp:undefined,
      mintemp:undefined,
      desc:undefined,
      weathericon:undefined,

    };
    this.getWeather();
    this.weathericon = {
      "Thunderstorm": "wi-thunderstorm",
      "Drizzle": "wi-sleet",
      "Rain":"wi-storm-showers",
      "Snow":"wi-snow",
       "Atmosphere":"wi-fog",
       "Clear":"wi-day-sunny",
       "Clouds":"wi-day-fog"

    };


  }

calCelsius(temp){
  let cel = Math.floor(temp-273.15);
  return cel;
}

getWeatherIcon(icon,range){
  switch(true){
    case range>=200 && range <= 232:
        this.setState({icon:this.weathericon.Thunderstorm})
        break;
    case range>=300 && range <= 321:
      this.setState({icon:this.weathericon.Drizzle})
      break;
    case range>=500 && range <= 531:
      this.setState({icon:this.weathericon.Rain})
      break;
    case range>=600 && range <= 622:
      this.setState({icon:this.weathericon.Snow})
      break;
    case range>=701 && range <= 781:
      this.setState({icon:this.weathericon.Atmosphere})
    break;
    case range === 800:
      this.setState({icon:this.weathericon.Clear})
    break;
    case range>=801 && range <= 804:
      this.setState({icon:this.weathericon.Clear})
    break;
  }

}

  getWeather = async() =>{
    const weatherapi = await fetch('https://api.openweathermap.org/data/2.5/weather?q=Ahmedabad,in&APPID=' +key+ '&units=metric')
    const response = await weatherapi.json();
    console.log(response);

    this.setState({
      city : response.name,
      country : response.sys.country,
      icon: undefined,
      main: response.main,
      celsius:response.main.temp,
      maxtemp:response.main.temp_max,
      mintemp:response.main.temp_min,
      desc:response.weather[0].main,
      icon:this.weathericon.Thunderstorm
     })

     this.getWeatherIcon(this.weathericon,)
  }

  render(){
    return(
      <div className="App">
      <header className="App-header">
        <WeatherWidget
         city={this.state.city} 
         country={this.state.country}
         temp_celsius={this.state.celsius}
         maxtemp = {this.state.maxtemp}
         mintemp = {this.state.mintemp}
         desc= {this.state.desc}
         weathericon = {this.state.icon}
         ></WeatherWidget>
      </header>
    </div>
    );
  }
}




export default App;
