import React from 'react';
import DayCard from './DayCardComponent.js';
const key = '7c16ffeea2411e0f96e0d6420116db57';
class WeekContainer extends React.Component {
  
  state = {
    fullData: [],
    dailyData: []
  }

 
  componentDidMount = () => {
    const weatherURL =
    `http://api.openweathermap.org/data/2.5/forecast?zip=11102&units=imperial&APPID=`+key+ `&units=metric`

    fetch(weatherURL)
    .then(res => res.json())
    .then(data => {
      const dailyData = data.list.filter(reading => reading.dt_txt.includes("18:00:00"))
      this.setState({
        fullData: data.list,
        dailyData: dailyData
      }, () => console.log(this.state))
    })
  }

  render() {
    return (
      <div>
        
      </div>
    )
  }
}

export default WeekContainer;
